package basics;

public class EvenNumbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int number=100;
System.out.print("List of Even numbers from 20 to "+number+" : ");
for(int i=20;i<=number;i++) {  //logic to be checked if the number is even or not
	if(i%2==0)  //if i%2 is equal to ZERO, the number is even
	{
		System.out.print(i+" ");
		
	}
	
}
	}

}
